const color = prompt('Enter 1 color or combine 2 colors');
const getColors = (color);

// color constructuring
const purple = 'red + blue';
const orange = 'red + yellow';
const green = 'blue + yellow';

// color deconstructing
const redBlue = 'purple';
const redYellow = 'orange';
const blueYellow = 'green'; 

if (getColors === purple){
    console.log('Purple');
}
else if (getColors === orange){
    console.log('Orange');
}
else if (getColors === green){
    console.log('Green');
}
else if (getColors === redBlue){
    console.log('Red + Blue');
}
else if (getColors === redYellow){
    console.log('Red + Yellow');
}
else if (getColors === blueYellow){
    console.log('Blue + Yellow');
}
else{
    console.log('Error');
}